// Usage: enter mongo shell, load("/mongo/MapReduce.js")

// connect to mongodb server
conn = new Mongo("localhost:27017");

// use database
db = conn.getDB("dev");

// define a mapper
var mapper = function() {
    var x = this.x.toLowerCase();
    var y = this.y.toLowerCase();
    var token = /^[a-z.-]+$/i.test(x) && /^[a-z.-]+$/i.test(y) ? x+'#'+this.xPos+'_'+y+'#'+this.yPos : "<non-english>";
    emit(token, 1);
};

// define reducer
var reducer = function(dep, count) {
    return Array.sum(count);
};

// perform mapReduce
db.deps.mapReduce(
        mapper,
        reducer,
        { out: "dep_pair_count_pure" } // output collection name
)
```
